document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".snippet-ref"),t=document.querySelectorAll(".snippet");if(e.forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const s=this.getAttribute("data-snippet"),n=document.getElementById("snippet-"+s);if(n)if(t.forEach(e=>e.classList.remove("active")),n.classList.add("active"),window.innerWidth>=1280)n.scrollIntoView({behavior:"smooth",block:"center"});else{const e=document.getElementById("mobile-snippet-"+s);e&&e.scrollIntoView({behavior:"smooth",block:"center"})}})}),window.innerWidth>=1280){const n={rootMargin:"-20% 0px -70% 0px"},s=new IntersectionObserver(e=>{e.forEach(e=>{if(e.isIntersecting){const s=e.target.getAttribute("data-snippet"),n=document.getElementById("snippet-"+s);n&&(t.forEach(e=>e.classList.remove("active")),n.classList.add("active"))}})},n);e.forEach(e=>s.observe(e))}})